INSERT INTO `state` VALUES (1,256,'Uganda',NULL,'upload/Uganda.txt',NULL,NULL,NULL,NULL,NULL,'2015-05-17 16:51:31','	<h1 id=\"firstHeading\" class=\"firstHeading\" lang=\"en\">Uganda</h1>\n						<div id=\"bodyContent\" class=\"mw-body-content\">\n									<div id=\"siteSub\"></div>\n								<div id=\"contentSub\"></div>\n												<div id=\"jump-to-nav\" class=\"mw-jump\">\n					Jump to:					<a href=\"http://en.wikipedia.org/wiki/Uganda#mw-head\">navigation</a>, 					<a href=\"http://en.wikipedia.org/wiki/Uganda#p-search\">search</a>\n				</div>\n				<div id=\"mw-content-text\" lang=\"en\" dir=\"ltr\" class=\"mw-content-ltr\"><table class=\"infobox geography vcard\" style=\"width:22em; font-size:88%;\">\n<tr class=\"adr\">\n<th colspan=\"3\" align=\"center\" class=\"mergedtoprow\" style=\"padding:0.25em 0.33em 0.33em;line-height:1.2em;font-size:1.25em;\"><span class=\"fn org country-name\">Republic of Uganda</span><br />\n<div style=\"padding-top:0.25em;\"><i>Jamhuri ya Uganda</i></div>\n</th>\n</tr>\n<tr class=\"mergedtoprow\">\n<td class=\"maptable\" colspan=\"3\" align=\"center\" style=\"padding:0.5em 0;\">\n<table align=\"center\" style=\"width:100%;background:none;\">\n<tr>\n<td align=\"center\" style=\"width:58%; vertical-align:middle;\"><a href=\"http://en.wikipedia.org/wiki/File:Flag_of_Uganda.svg\" class=\"image\" title=\"Flag of Uganda\"><img alt=\"\" src=\"//upload.wikimedia.org/wikipedia/commons/thumb/4/4e/Flag_of_Uganda.svg/125px-Flag_of_Uganda.svg.png\" width=\"125\" height=\"83\" class=\"thumbborder\" srcset=\"//upload.wikimedia.org/wikipedia/commons/thumb/4/4e/Flag_of_Uganda.svg/188px-Flag_of_Uganda.svg.png 1.5x, //upload.wikimedia.org/wikipedia/commons/thumb/4/4e/Flag_of_Uganda.svg/250px-Flag_of_Uganda.svg.png 2x\" data-file-width=\"900\" data-file-height=\"600\" /></a></td>\n<td align=\"center\" style=\"width:auto;vertical-align:middle;\"><a href=\"http://en.wikipedia.org/wiki/File:Coat_of_arms_of_the_Republic_of_Uganda.svg\" class=\"image\" titl